import React, { createContext, useState, useContext, useEffect } from 'react';
import axios from 'axios';

// Konfiguracja klienta API
const apiClient = axios.create({
  baseURL: "http://localhost:5000",
  withCredentials: true,
});

// Tworzymy kontekst
const AuthContext = createContext(null);

// Tworzymy "Providera" - komponent, który będzie dostarczał stan
export const AuthProvider = ({ children }) => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [isLoading, setIsLoading] = useState(true); // Dodajemy stan ładowania

  useEffect(() => {
    // Sprawdzamy przy pierwszym ładowaniu aplikacji, czy użytkownik ma aktywną sesję na backendzie
    const checkAuthStatus = async () => {
      try {
        await apiClient.get('/api/check-auth');
        setIsAuthenticated(true); // Jeśli zapytanie się powiedzie (status 200), użytkownik jest zalogowany
      } catch (error) {
        setIsAuthenticated(false); // Jeśli błąd (np. 401), użytkownik nie jest zalogowany
      } finally {
        setIsLoading(false); // Kończymy ładowanie
      }
    };

    checkAuthStatus();
  }, []);

  const logout = async () => {
    try {
      await apiClient.get('/api/logout');
      setIsAuthenticated(false);
    } catch (error) {
      console.error("Błąd podczas wylogowywania", error);
    }
  };

  // Wartości, które udostępniamy w całej aplikacji
  const value = { isAuthenticated, isLoading, logout };

  return (
    <AuthContext.Provider value={value}>
      {children}
    </AuthContext.Provider>
  );
};

// Hook ułatwiający korzystanie z kontekstu w innych komponentach
export const useAuth = () => {
  return useContext(AuthContext);
};
