import React, { useState } from 'react';
import axios from 'axios';

function App() {
  const [loggedIn, setLoggedIn] = useState(false);
  const [userData, setUserData] = useState(null);

  const handleLogin = async () => {
    // Otwórz okno logowania Spotify (przekierowanie do backendu po auth_url)
    window.location.href = 'http://localhost:5000/get_logged';
  };

  const handleLogout = async () => {
    try {
      await axios.post('http://localhost:5000/clear_session');
      setUserData(null);
      setLoggedIn(false);
    } catch (error) {
      console.error('Error logging out:', error);
    }
  };

  React.useEffect(() => {
    // Sprawdź czy użytkownik jest zalogowany po powrocie z autoryzacji
    axios.get('http://localhost:5000/get_logged').then(res => {
      if (res.data.logged_in) {
        setUserData(res.data.user_info ? res.data : { user_info: res.data });
        setLoggedIn(true);
      }
    });
  }, []);

  return (
    <div>
      {loggedIn ? (
        <div>
          <h1>Welcome, {userData?.user_info?.display_name}</h1>
          <p>ID: {userData?.user_info?.id}</p>
          <p>URI: {userData?.user_info?.uri}</p>
          <a href={userData?.user_info?.profile_url}>Profile Link</a>
          <br />
          <button onClick={handleLogout}>Wyloguj</button>
        </div>
      ) : (
        <div>
          <h1>Not logged in</h1>
          <button onClick={handleLogin}>Login with Spotify</button>
        </div>
      )}
    </div>
  );
}

export default App;
