import React, { useState, useEffect } from 'react';

const Dashboard = ({ user }) => {
  const [topTracks, setTopTracks] = useState([]);
  const [error, setError] = useState('');
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    const fetchTopTracks = async () => {
      try {
        const res = await fetch('/api/top-tracks', { credentials: 'include' });
        if (!res.ok) throw new Error('Nie udało się pobrać topowych utworów.');
        const data = await res.json();
        setTopTracks(data.items || []);
      } catch (err) {
        setError(err.message);
      } finally {
        setIsLoading(false);
      }
    };
    fetchTopTracks();
  }, []);

  const handleLogout = () => {
    window.location.href = 'http://127.0.0.1:5000/logout';
  };

  return (
    <div style={{ padding: '20px', backgroundColor: '#121212', color: 'white', minHeight: '100vh' }}>
      <div style={{ textAlign: 'center', marginBottom: '40px' }}>
        <h1>Witaj, {user.display_name}!</h1>
        {user.images?.[0]?.url && (
          <img src={user.images[0].url} alt="Avatar" style={{ width: '150px', height: '150px', borderRadius: '50%' }} />
        )}
        <p>Email: {user.email}</p>
        <button onClick={handleLogout} style={{color: 'white', background: '#1DB954', border: 'none', padding: '10px 20px', borderRadius: '20px', cursor: 'pointer'}}>Wyloguj</button>
      </div>
      <div>
        <h2 style={{ textAlign: 'center', marginBottom: '30px' }}>Twoje topowe utwory</h2>
        {isLoading && <p>Ładowanie utworów...</p>}
        {error && <p style={{ color: 'red' }}>{error}</p>}
        <ol style={{ listStyle: 'none', padding: 0, maxWidth: '600px', margin: '0 auto' }}>
          {topTracks.map((track, index) => (
            <li key={track.id} style={{ display: 'flex', alignItems: 'center', marginBottom: '15px', backgroundColor: '#282828', padding: '10px', borderRadius: '5px' }}>
              <span style={{ marginRight: '15px', fontSize: '1.2rem', minWidth: '30px' }}>{index + 1}.</span>
              <img src={track.album.images[0]?.url} alt={track.name} style={{ width: '60px', height: '60px', marginRight: '15px' }} />
              <div>
                <div style={{ fontWeight: 'bold' }}>{track.name}</div>
                <div style={{ color: '#b3b3b3' }}>{track.artists.map(artist => artist.name).join(', ')}</div>
              </div>
            </li>
          ))}
        </ol>
      </div>
    </div>
  );
};
export default Dashboard;
