// ... (importy)
import { useAuth } from '../context/AuthContext'; // Importujemy hooka useAuth
import { useNavigate } from 'react-router-dom'; // Do przekierowania po wylogowaniu

const DashboardPage = () => {
  const { logout } = useAuth();
  const navigate = useNavigate();

  // Ta funkcja jest wywoływana TYLKO po kliknięciu przycisku
  const handleLogout = async () => {
    await logout(); // Wywołuje funkcję z AuthContext
    navigate('/');   // Przekierowuje na stronę główną
  };

  return (
    <div className="dashboard-container">
      <header className="dashboard-header">
        {/* ... */}
        {/* Przycisk, który uruchamia całą akcję */}
        <button onClick={handleLogout} className="logout-button">
          Wyloguj
        </button>
      </header>
      {/* ... reszta kodu ... */}
    </div>
  );
};
